// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract SimpleLottery {
    address public manager;
    address[] public players;
    
    constructor() {
        manager = msg.sender;
    }
    
    function enter() public payable {
        require(msg.value > 0.01 ether, "Minimum entry fee is 0.01 ether");
        players.push(msg.sender);
    }
    
    function getPlayers() public view returns (address[] memory) {
        return players;
    }
    
    function getBalance() public view returns (uint) {
        return address(this).balance;
    }
    
    function random() private view returns (uint) {
        return uint(keccak256(abi.encodePacked(block.prevrandao, block.timestamp, players)));
    }
    
    function pickWinner() public restricted {
        require(players.length > 0, "No players in the lottery");
        
        uint index = random() % players.length;
        address payable winner = payable(players[index]);
        
        // Transfer 90% to winner
        uint winnerAmount = address(this).balance * 90 / 100;
        uint managerFee = address(this).balance - winnerAmount;
        
        winner.transfer(winnerAmount);
        payable(manager).transfer(managerFee);
        
        // Reset the lottery
        players = new address[](0);
    }
    
    modifier restricted() {
        require(msg.sender == manager, "Only manager can call this function");
        _;
    }
}
